function App(props) {

  useEffect(()=> {
    props.retreiveSmurf()
  },[])



    return (
      <div className="App">
        <h1>SMURFS! 2.0 W/ Redux</h1>
        <SmurfForm />
      </div>
    );
}

export default connect(null,{retreiveSmurf})(App);
